import React, {useState} from "react";

export const Register = () => {
    const [email, setEmail] = useState('');
    const [pass , setPass] = useState('');
    const [fullname, setFullname] = useState('');
    const [phone, setPhone] = useState('');

    //now capture the data when User submit the form
    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(email);
    } 
    return(
        <div>
            {/* Register Form */}
            <form onSubmit={handleSubmit}>

                  {/* Other related texts & links */}
                <div className="d-flex">
                    <h3>به پلاکارد خوش آمدید</h3>
                    <h3>حساب کاربری دارید؟</h3>
                    <a href="">وارد شوید</a>
                </div>
                {/* the login part */}
                <div className='text-part'> 
                    <h1>ثبت نام</h1>
                    <label htmlFor="email">ایمیل خود را وارد کنید</label>    
                    <input value={email} onChange={(e) => setEmail(e.target.value)} type='email' placeholder= "Sonbol@gmail.com" />

                    <label htmlFor="phoneNumber">شماره تلفن همراه </label>
                    <input value={phone} onChange={(e) => setPhone(e.target.value)} type='phoneNumber' placeholder= "09123456789" />

                    <label htmlFor="fullName">نام و نام خانوادگی</label>
                    <input value={fullname} onChange={(e) => setPass(e.target.value)} type='name' placeholder="محمد محمدی" />

                    <label htmlFor="password">رمزعبور خود را وارد کنید</label>
                    <input value={pass} onChange={(e) => setPass(e.target.value)} type='password' placeholder="********" />

                    <button type="submit">ورود</button>

                </div>
            </form>
        </div>
    )   
}

export default Register
