export const Register = () => {
    const [email, setEmail] = useState('');
    const [pass , setPass] = useState('');
    const [fullname, setFullname] = useState('');

    //now capture the data when User submit the form
    const handleSubmit = (e) => {
        e.preventDefault();
        console.log(email);
    } 
    return(
        <div>
            {/* Register Form */}
            <form onSubmit={handleSubmit}>

                 {/* the login part */}
                <div className='text-part'> 
                   <h3>به پلاکارد خوش آمدید</h3>
                    <h1>ثبت نام</h1>
                    <label htmlFor="email">ایمیل خود را وارد کنید</label>                        <input value={email} onChange={(e) => setEmail(e.target.value)} type='email' placeholder= "Sonbol@gmail.com" />
                    <label htmlFor="password">رمزعبور خود را وارد کنید</label>
                    <input value={pass} onChange={(e) => setPass(e.target.value)} type='password' placeholder="********" />

                    <button type="submit">ورود</button>

                    {/* Other related texts & links */}
                    <div className="d-flex">
                        <h3>حساب کاربری ندارید؟</h3>
                       <a href="">ثبت‌ نام کنید</a>
                    </div>
                </div>
            </form>
        </div>
    )   
}

export default Register
